DECLARE @RETURN_VALUE_ARGUMENT DECIMAL
EXECUTE [dbo].[GET_NEW_LOADID]
@RETURN_VALUE_ARGUMENT OUTPUT
,@P_DATA_LOCATION_NAME = 'Retail_CustomerMaster'
,@P_PROGRAM_NAME = @{concat('Delete',pipeline().parameters.EntityName)}
,@P_LOAD_DESCRIPTION = @{concat('Delete',pipeline().parameters.EntityName)}
,@P_USERNAME = 'semadmin'
SELECT @RETURN_VALUE_ARGUMENT AS RETURN_VALUE_ARGUMENT

select top 1 Id,ChunkNumber,TableName,Organization
from Masterdata_CDP.PreferenceInitialLoadChunks with(nolock)
where isloaded = 0 and ProcessedDate is NULL and channel ='email'

select top 1 Id,ChunkNumber,TableName,Organization
from Masterdata_CDP.PreferenceInitialLoadChunks with(nolock)
where isloaded = 0 and ProcessedDate is NULL and channel = 'SMS'

Update a set ProcessedDate = getdate() , isloaded = 1
from Masterdata_CDP.PreferenceInitialLoadChunks a with(nolock)
WHERE ChunkNumber = @{activity('Get_Email_Chunk').output.firstRow.ChunkNumber} and TableName = '@{activity('Get_Email_Chunk').output.firstRow.TableName}' and Organization = '@{activity('Get_Email_Chunk').output.firstRow.Organization}' and channel = 'email';
select 1 as success;

Update a set ProcessedDate = getdate() , isloaded = 1
from Masterdata_CDP.PreferenceInitialLoadChunks a with(nolock)
WHERE ChunkNumber = @{activity('Get_Phone_Chunk').output.firstRow.ChunkNumber} and TableName = '@{activity('Get_Phone_Chunk').output.firstRow.TableName}' and Organization = '@{activity('Get_Phone_Chunk').output.firstRow.Organization}' and channel = 'SMS';
select 1 as success;
