select top 1 Id,ChunkNumber,TableName,Organization
from Masterdata_CDP.PreferenceInitialLoadChunks with(nolock)
where isloaded = 0 and ProcessedDate is NULL and channel ='email'

select top 1 Id,ChunkNumber,TableName,Organization
from Masterdata_CDP.PreferenceInitialLoadChunks with(nolock)
where isloaded = 0 and ProcessedDate is NULL and channel = 'Phone'

Update a set ProcessedDate = getdate() , isloaded = 1
from Masterdata_CDP.PreferenceInitialLoadChunks a with(nolock)
WHERE ChunkNumber = @{activity('Get_Email_Chunk').output.firstRow.ChunkNumber} and TableName = '@{activity('Get_Email_Chunk').output.firstRow.TableName}' and Organization = '@{activity('Get_Email_Chunk').output.firstRow.Organization}' and channel = 'email';
select 1 as success;

